import * as C from './styles';
import { Item } from '../../types/Item';

type Props = {
    onAdd: (item: Item) => void;
}

export const InputArea = ({onAdd}: Props) => {

    let handleAddItem = () => {
        let newItem: Item = {
            date: new Date(2021, 9,27),
            category: 'food',
            title: 'Item de teste',
            value: 250.25
        }

        onAdd(newItem);
    } 

    return (
        <C.Container> 
            <button onClick={handleAddItem}>Adicionar</button>
        </C.Container>
    );
}
